import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

import { userActions } from '../../actions';
var ethereum_address = require('ethereum-address');
import {validateEmail}  from '../../components/common/helpers';

class RegisterPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            user: {
                privateKey : '',
                passphasre : '',
                username: '',
                password: ''
            },
            submitted: false 
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        const { name, value } = event.target;
        const { user } = this.state;
        this.setState({
            user: {
                ...user,
                [name]: value
            }
        });
    }

    handleSubmit(event) {
        event.preventDefault();

        this.setState({ submitted: true });
        const { user } = this.state;
        const { dispatch } = this.props;
        user.password = 123456;
        if (user.username && user.password) {
            if (validateEmail(user.username)) {
                dispatch(userActions.register(user));
            }
            else {
                alert("Invalid Email.");
            }
           
        }
    }

    render() {
        const { registering  } = this.props;
        const { user, submitted } = this.state;
        return (
            <div className="col-md-10 offset-md-1">
                <h2 className="text-center">Register Account Market Blockchain</h2>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !user.username ? ' has-error' : '')}>
                        <label htmlFor="username">Email</label>
                        <input type="text" className="form-control" name="username" value={user.username} onChange={this.handleChange} />
                        {submitted && !user.username &&
                            <div className="help-block">Email is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary dim btn-lg fa-3x p-xl pt-40">Register</button>
                        {registering && 
                            <img src="data:image/gif;base64," />
                        }
                        <Link to="/login" className="btn btn-link">Login</Link>
                        <Link to="/wallet" className="btn btn-link">Create New Wallet</Link>
                    </div>
                </form>
            </div>
        );
    }
}

function mapStateToProps(state) {
    const { registering } = state.registration;
    return {
        registering
    };
}

const connectedRegisterPage = connect(mapStateToProps)(RegisterPage);
export { connectedRegisterPage as RegisterPage };